import React from 'react';
import PropTypes from 'prop-types';
import './styles/controls.css'


const Controls = (props) => (
                <div className="controls">
                    {
                        props.status !== 'started' &&
                        <button className="btn btn-lg btn-success start"
                            disabled={!props.canStart}
                            onClick={props.startTimer}>
                            <div className="start">
                                <i class="fas fa-play"></i>
                            </div>
                        </button>
                    }

                    {
                        props.status === 'started' &&
                        <button className="btn btn-lg btn-dark stop"
                            onClick={props.stopTimer}
                            style = {{'margin-top': '18rem'}}
                            >
                            <div className="stop">
                                <i class="fas fa-stop"></i>
                            </div>
                        </button>
                    }
                    <button className="btn btn-lg btn-primary"
                        onClick={props.resetTimer}
                        style = {props.status === 'started'? {'margin-top': '18rem'}:{'margin-top': '4rem'}}
                        >
                        <div className="refresh">
                          <i class="fas fa-sync"></i>
                        </div>
                    </button>
                </div>
);

Controls.defaultProps = {
    startTimer: () => alert('startTimer'),
    stopTimer: () => alert('stopTimer'),
    resetTimer: () => alert('resetTimer'),
    status: null,
    canStart: false,
};

Controls.propTypes = {
    startTimer: PropTypes.func,
    stopTimer: PropTypes.func,
    resetTimer: PropTypes.func,
    status: PropTypes.string,
    canStart: PropTypes.bool
};

export default Controls;
