import React, { Component } from 'react';
import './App.css';
import TimerForm from '../timerform/TimerForm';
import TimerList from '../timerlist/TimerList';

class App extends Component {
  state ={
    timers : [],
    timerName:''
  }
    addNewTimer = (timerData) =>{
    this.setState(prevState => ({
      timers: [...prevState.timers, timerData],
    }));
  };
  onChange = (event) => {
    this.setState({ timerName : event.target.this.state.timerName })}
  render(){
  return (
    <div className="App">
      <header className="App-header">
         <i class="fas fa-hourglass-half"></i><span> React Timer</span>
      </header>
      <TimerForm
      timerName = {this.state.timerName}
      onChange = {this.onChange}
      onSubmit={this.addNewTimer}
       />
      <TimerList
      timers ={this.state.timers}
       />
    </div>
  );
}
}

export default App;
