import React, { Component } from 'react';
import './App.css';
import TimerList from '../timerlist/TimerList';

class App extends Component {
      constructor() {
            super();
            this.state = {
              timers : [],
              timerName: ''
        };
        this.addNewTimer = this.addNewTimer.bind(this);
      }
  addNewTimer(timerData) {
    this.setState(prevState => ({
      timers: [...prevState.timers, timerData],
      timerName: ""
    }));

  }
  render(){
      return (
        <div className="App">
          <header className="App-header">
            <i class="fas fa-hourglass-half"></i><span> React Timer</span>
          </header>
          <div className="conatiner">
            <div className="input-name">
              <input
                type="text"
                placeholder="Create Timer"
                maxLength="10"
                value={this.state.timerName}
                onChange={event => this.setState({ timerName : event.target.value })}
                placeholder="" required
              />
              <button onClick={this.addNewTimer}>Add Timer</button>
            </div>
          </div>
          <TimerList
          timers = {this.state.timers}
          timerName = {this.state.timerName}
          />
        </div>
      );
  }
}

export default App;
