import React from 'react';
import PropTypes from 'prop-types';
import Time from '../time/Time';

const Display = (props) => {
    const time = new Time();
    const onChange = (e) => {
        props.onSecondsChanged(e.target.value);
    };
   return (
            <div className='display'>
                <div className="display-time">{time.getTime(props.time)}</div>
                <div className="input-time">
                <input 
                className = 'align-self-center display-time'
                maxLength = '6'
                value = {props.seconds}
                onChange = {onChange}
                />
                </div>
            </div>
        );
    }
Display.defaultProps = {
    seconds: 0,
    status: null,
    time: 0,
    onSecondsChanged: (e) => console.log(e.target.value)
};

Display.propTypes = {
    children: PropTypes.element,
    seconds: PropTypes.number.isRequired,
    status: PropTypes.string,
    time: PropTypes.number,
    onSecondsChanged: PropTypes.func
};
export default Display
