import React from 'react';
import PropTypes from 'prop-types';

const Display = (props) => {
    const time = new Date();
    const onChange = (e) => {
        props.onSecondChange(e.target.value);
    };
   return (
            <div className='display'>
                <div className="display-time">{time.getTime(props.time)}</div>
                <div className="input-time">
                <input 
                className = 'align-self-center display-time'
                maxLength = '6'
                value = {props.seconds}
                onChange = {onChange}
                />
                </div>
            </div>
        );
    }

Display.defaultProps = {
    seconds: 0,
    onSecondChanged: (e) => console.log(e.target.value)
};
Display.propTypes = {
    seconds: PropTypes.number.isRequired
}
export default Display
