import React from 'react';
import PropTypes from 'prop-types';

const Display = (props) => {
    const onChange = (e) => {
        props.onSecondChange(e.target.value);
    };
     const getTime = (timeInMilliseconds) => {

        let time = timeInMilliseconds;

        const hours = this.formatUnitOfTime(Math.floor( time / (60 * 60 * 1000)));

        time = time % (60 * 60 * 1000);
        const minutes = this.formatUnitOfTime(Math.floor( time / (60 * 1000)));

        time = time % (60 * 1000);
        const seconds = this.formatUnitOfTime(Math.floor( time / 1000 ));

        const milliseconds = this.formatUnitOfTime(time % 1000);

        return `${hours}:${minutes}:${seconds}:${milliseconds}`;
    }

    const formatUnitOfTime = (unitOfTime) => {
        return unitOfTime < 10 ? `0${unitOfTime}`.substring(0,2) : unitOfTime.toString().substring(0,2);
    }
   return (
            <div className='display'>
                <div className="display-time">{getTime(props.time)}</div>
                <div className="input-time">
                <input 
                className = 'align-self-center display-time'
                maxLength = '6'
                value = {props.seconds}
                onChange = {onChange}
                />
                </div>
            </div>
        );
    }

Display.defaultProps = {
    seconds: 0,
    onSecondChanged: (e) => console.log(e.target.value)
};
Display.propTypes = {
    seconds: PropTypes.number.isRequired
}
export default Display
