import React, { Component } from 'react';
import './App.css';
import TimerList from '../timerlist/TimerList';


var timerName;
class App extends Component {
      constructor() {
            super();
            this.state = {
              timers : [],
        };
        this.addNewTimer = this.addNewTimer.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
      }
  addNewTimer(timerData) {
    this.setState(prevState => ({
      timers: [...prevState.timers, timerData]
    }));
  }

    onSubmit(event) {
    event.preventDefault();
    timerName = this.refs.timerName.value;
    this.refs.form.reset();
    }
  render(){
      return (
        <div className="App">
          <header className="App-header">
            <i class="fas fa-hourglass-half"></i><span> React Timer</span>
          </header>
          <div className="conatiner">
            <div className="input-name">
            <form ref="form" onSubmit={this.onSubmit} className="form-inline">
              <input
                type="text"
                placeholder="Create Timer"
                maxLength="10"
                ref = 'timerName'
                placeholder="" required
              />
              <button
              type = 'submit'
              onClick = {this.addNewTimer}
              >Add Timer</button>
              </form>
            </div>
          </div>
          <TimerList
          timers = {this.state.timers}
          timerName = {this.timerName}
          />
        </div>
      );
  }
}

export default App;
