import React, { Component } from "react";
import Display from "./Display";
import Controls from "./Controls";

class Timer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      seconds: 0,
      time: 0,
      status: null
    };
    this.handleChange = this.handleChange.bind(this);
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
    this.resetTimer = this.resetTimer.bind(this);
  }

    handleChange(seconds) {
        seconds = parseInt(seconds);

        if (seconds && typeof seconds === 'number') {
            if (seconds <= 359999) {
                this.setState(() => ({ seconds: seconds, time: seconds * 1000 }));
            }
        } else {
            this.setState(() => ({ seconds: 0, time: 0 }));
        }
    }
    startTimer(){
        if (this.state.status !== 'started'){
            this.interval = setInterval(() => {
                if (this.state.time !== 0){
                    this.setState(prevState => ({time: prevState.time -10}));
                }
                else{
                    this.setState(() => ({ seconds: 0, status: null, time: 0 }));
                    clearInterval(this.interval)
                }
            this.setState(() => ({status : 'started'}));
            },10);
        }
        this.setState(() => {status :null})
    }

    stopTimer(){
        if (this.state.status && this.state.status === 'started'){
            clearInterval(this.interval);
            this.setState((prevState) => ({status: 'stopped', seconds: Math.floor(prevState.time / 1000) }))
        }
    }

    resetTimer(){
        clearInterval(this.interval);
        this.setState(() => ({seconds:0, time:0, status: null}))
    }
  render() {
    return (
      <div className="clock">
        <div className="time"></div>
        <Display
          seconds={this.state.seconds}
          time = {this.state.time}
          status = {this.state.status}
          onSecondChange={this.handleChange}
        />
        <Controls
        startTimer = {this.startTimer}
        stopTimer = {this.stopTimer}
        resetTimer = {this.resetTimer}
        canStart = {this.state.seconds > 0}
         />
      </div>
    );
  }
}

export default Timer;
