import React from 'react';
import PropTypes from 'prop-types';
import Time from '../time/Time';
import './styles/display.css';


const Display = (props) => {
    const time = new Time();
    const onChange = (e) => {
        props.onSecondsChanged(e.target.value);
    };
   return (
       <div className="clock">
        { 
                props.status === 'started' &&
                <div className="display-time"
                style = {{color: props.time <= 10000 ? '#FE5C5C' : ''}}
                >
                <span className="ends">Ends In</span>
                {time.getTime(props.time)}
                </div> 
        }
        {
                props.status !== 'started' && 
                <div className="initial-input">
                    <div className="display-in-time">
                    {time.getTime(props.time)}
                    </div>         
                    <div className="input-time">
                    <input 
                        className = ''
                        maxLength = '6'
                        value = {props.seconds}
                        onChange = {onChange}
                    />
                    </div>
                </div>
            }
            <div>
                {props.children}
            </div>
        </div>
        );
    }
Display.defaultProps = {
    seconds: 0,
    status: null,
    time: 0,
    onSecondsChanged: (e) => console.log(e.target.value)
};

Display.propTypes = {
    children: PropTypes.element,
    seconds: PropTypes.number.isRequired,
    status: PropTypes.string,
    time: PropTypes.number,
    onSecondsChanged: PropTypes.func
};
export default Display
