import React, { Component } from 'react';
import './App.css';
import TimerForm from '../timerform/TimerForm';
import TimerList from '../timerlist/TimerList';

class App extends Component {
      constructor() {
            super();
            this.state = {
              timers : [],
              timerName: ''
        };
        this.addNewTimer = this.addNewTimer.bind(this);
        this.onChange = this.onChange.bind(this);
      }
  addNewTimer(timerData) {
    this.setState(prevState => ({
      profiles: [...prevState.timers, timerData]
    }));
  }
    onChange(event) {
      this.setState(prevState => 
      ({timerName : event.target.state.timerName
      }));
    }
  render(){
  return (
    <div className="App">
      <header className="App-header">
         <i class="fas fa-hourglass-half"></i><span> React Timer</span>
      </header>
      <TimerForm
      timerName = {this.state.timerName}
      onChange = {this.onChange}
      onSubmit={this.addNewTimer}
       />
      <TimerList
      timers ={this.state.timers}
       />
    </div>
  );
}
    }

export default App;
