import React, { Component } from 'react';
import './App.css';
import TimerForm from '../timerform/TimerForm';
import TimerList from '../timerlist/TimerList';

class App extends Component {
      constructor() {
            super();
            this.state = {
              timers : [],
              timerName: ''
        };
        this.addNewTimer = this.addNewTimer.bind(this);
      }
  addNewTimer(timerData) {
    this.setState(prevState => ({
      profiles: [...prevState.timers, timerData]
    }));
  }
  render(){
  return (
    <div className="App">
      <header className="App-header">
         <i class="fas fa-hourglass-half"></i><span> React Timer</span>
      </header>
      <div className="conatiner">
        <div className="input-name">
          <input
            type="text"
            placeholder="Create Timer"
            maxLength="20"
            value = {this.state.timerName}
            onChange={this.setState(event => ({timerName: event.target.value}))}
          />
          <button onClick={this.addNewTimer}>Add Timer</button>
        </div>
      </div>
      <TimerList
      timers ={this.state.timers}
       />
    </div>
  );
}
    }

export default App;
