import React, { Component } from 'react'
import Display from './Display';
import Controls from './Controls';

class Timer extends Component {
    constructor(props) {
        super(props);
        this.sate = {
            seconds : 0
        }
    }

    render() {
        return (
            <div className='clock'>
                <div className="time"></div>
                <Display 
                seconds = {this.state.seconds}
                />
                <Controls />
            </div>
        )
    }
}

export default Timer
